$(document).ready(function () {
    fitText();
});

function fitText() {
    $('#headline h2').fitText(1.55, {
        minFontSize: 32
    });
    $('#headline p').fitText(5.57);
    $('#categories a').fitText(1.233333, {
        maxFontSize: 27
    });
    $('.values h2').fitText(1, {
        minFontSize: 18,
        maxFontSize: 33
    })
}